import React from "react";
import { useHistory } from "react-router-dom";
import {
    Flex, 
    Button, 
    Table, 
    TableHeader, 
    TableHeaderCell,
    TableBody,
    TableRow,
    TableCell,
    Heading,
    ExpandCollapse,
    Text } from "monday-ui-react-core";
import "../App.css";

const OrdersBoard = () => {
    const history = useHistory();
    const homepage = () => (
        history.push("/")
    )

    const columns = [{
        id: "item",
        title: "Item",
        width: 180,
        loadingStateType: "medium-text"
    }, {
        id: "salesAssociate",
        title: "Sales Associate",
        width: 200,
        loadingStateType: "long-text"
    }, {
        id: "scentProfiles",
        title: "Scent Profiles",
        width: 200,
        loadingStateType: "medium-text"
    }];

    const data = [{
        id: 1,
        item: "Pine",
        salesAssociate: "Zack",
        scentProfiles: ["Pine", "Mint", "Grass"]
    }, {
        id: 2,
        item: "Spring",
        salesAssociate: "Zack",
        scentProfiles: ["Flowers", "Breeze", "Trees"]
    }];

    const ExpandCollapseCustomHeadingComponent = () => {
        return <Heading type={Heading.types.H3}>Any component you want</Heading>;
    };

    return (
        <>
            <Button className="Back" onClick={homepage}>
                Back to Home
            </Button>
            <div className="App">
                <Flex gap={Flex.gaps.LARGE} direction={Flex.directions.COLUMN}>
                    <ExpandCollapse
                        title="Open Requests"
                        defaultOpenState
                        // headerComponentRenderer={ExpandCollapseCustomHeadingComponent}
                        // className={styles.storybookExpandCollapse}
                    >
                        <Table style={{
                            width: "auto"
                        }} size={Table.sizes.MEDIUM} columns={columns}>
                            <TableHeader>
                                {columns.map((headerCell, index) => 
                                    <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                                )}
                            </TableHeader>
                            <TableBody>
                                {data.map(rowItem => 
                                    <TableRow key={rowItem.id}>
                                        <TableCell>{rowItem.item}</TableCell>
                                        <TableCell>{rowItem.salesAssociate}</TableCell>
                                        <TableCell>{rowItem.scentProfiles.map(scent => scent)}</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </ExpandCollapse>
                    <ExpandCollapse
                        title="Working On It"
                        defaultOpenState
                        // headerComponentRenderer={ExpandCollapseCustomHeadingComponent}
                        // className={styles.storybookExpandCollapse}
                    >
                        <Table style={{
                            width: "auto"
                        }} size={Table.sizes.MEDIUM} columns={columns}>
                            <TableHeader>
                                {columns.map((headerCell, index) => 
                                    <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                                )}
                            </TableHeader>
                            <TableBody>
                                {data.map(rowItem => 
                                    <TableRow key={rowItem.id}>
                                        <TableCell>{rowItem.item}</TableCell>
                                        <TableCell>{rowItem.salesAssociate}</TableCell>
                                        <TableCell>{rowItem.scentProfiles}</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </ExpandCollapse>
                    <ExpandCollapse
                        title="Done"
                        defaultOpenState
                        // headerComponentRenderer={ExpandCollapseCustomHeadingComponent}
                        // className={styles.storybookExpandCollapse}
                    >
                        <Table style={{
                            width: "auto"
                        }} size={Table.sizes.MEDIUM} columns={columns}>
                            <TableHeader>
                                {columns.map((headerCell, index) => 
                                    <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                                )}
                            </TableHeader>
                            <TableBody>
                                {data.map(rowItem => 
                                    <TableRow key={rowItem.id}>
                                        <TableCell>{rowItem.item}</TableCell>
                                        <TableCell>{rowItem.salesAssociate}</TableCell>
                                        <TableCell>{rowItem.scentProfiles}</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </ExpandCollapse>
                </Flex>
                {/* <Table style={{
                        width: "auto"
                    }} size={Table.sizes.MEDIUM} columns={columns}>
                    <TableHeader>
                        {columns.map((headerCell, index) => 
                            <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                        )}
                    </TableHeader>
                    <TableBody>
                        {data.map(rowItem => 
                            <TableRow key={rowItem.id}>
                                <TableCell>{rowItem.item}</TableCell>
                                <TableCell>{rowItem.salesAssociate}</TableCell>
                                <TableCell>{rowItem.scentProfiles}</TableCell>
                            </TableRow>
                        )}
                    </TableBody>
                </Table> */}
            </div>
        </>
    );
};

export default OrdersBoard;
