          <div className="Text">
            <TextField id="category" placeholder="Category" title="Category" size={TextField.sizes.MEDIUM} requiredAsterisk={true} />
          </div>
          <div className="Text">
            <TextField id="imageUrl" placeholder="Image URL" title="Image URL" size={TextField.sizes.MEDIUM} requiredAsterisk={true} />
          </div>
          <div>
            <Button onClick={addNewCandle}>
              Add Candle
            </Button>
          </div>
        </Flex>
      </div>
    </>
  );
};

export default AddNewCandle;
