import React from "react";
import { useHistory } from "react-router-dom";
import { useMemo } from "react";
import {
    Flex, 
    Button, 
    Table, 
    TableHeader, 
    TableHeaderCell,
    TableBody,
    TableRow,
    TableCell,
    Heading,
    ExpandCollapse,
    Text } from "monday-ui-react-core";
import "../App.css";

const OrdersBoard = () => {
    const history = useHistory();
    const homepage = () => (
        history.push("/")
    )

    const columns = [{
        id: "item",
        title: "Item",
        width: 180,
        loadingStateType: "medium-text"
    }, {
        id: "salesAssociate",
        title: "Sales Associate",
        width: 200,
        loadingStateType: "long-text"
    }, {
        id: "scentProfiles",
        title: "Scent Profiles",
        width: 200,
        loadingStateType: "medium-text"
    }];

    const data = [{
        id: 1,
        item: "Pine",
        salesAssociate: "Zack",
        scentProfiles: ["Pine", "Mint", "Grass"]
    }, {
        id: 2,
        item: "Spring",
        salesAssociate: "Zack",
        scentProfiles: ["Flowers", "Breeze", "Trees"]
    }];

    const orders = [];
    async function getOrders() {
        const response = await fetch("http://localhost:8090/orders")
        const data = await response.json()
        data.forEach(element => {
            processData(element)
        });
    }
    getOrders()
    // .then((response) => response.json())
    // .then((json_response) => {
    //     var count = 1
    //     json_response.forEach(order => {
    //         orders.push({
    //             id: count,
    //             item: order.item,
    //             salesAssociate: order.salesAssociate,
    //             scentProfiles: order.scentProfiles,
    //             status: order.status
    //         })
    //         count++
    //     });
    // });

    // const orders_copy = orders
    // let items = orders.map(a => a.item)
    // console.log(items)
    // console.log(orders_copy)
    // console.log(orders_copy.length)

    function processData(data) {
        console.log(data)
    }

    return (
        <>
            <Button className="Back" onClick={homepage}>
                Back to Home
            </Button>
            <div className="App">
                <Flex gap={Flex.gaps.LARGE} direction={Flex.directions.COLUMN}>
                    <ExpandCollapse
                        title="Open Requests"
                        defaultOpenState
                        // headerComponentRenderer={ExpandCollapseCustomHeadingComponent}
                        // className={styles.storybookExpandCollapse}
                    >
                        <Table style={{
                            width: "auto"
                        }} size={Table.sizes.MEDIUM} columns={columns}>
                            <TableHeader>
                                {columns.map((headerCell, index) => 
                                    <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                                )}
                            </TableHeader>
                            <TableBody>
                                <TableRow>
                                    <TableCell>{[orders[0]]}</TableCell>
                                    <TableCell>Column Two</TableCell>
                                    <TableCell>Column Three</TableCell>
                                </TableRow>

                                {/* {orders.map(order => 
                                    <TableRow key={order.id}>
                                        <TableCell>{order.item}</TableCell>
                                        <TableCell>{"order.salesAssociate"}</TableCell>
                                        <TableCell>{order.scentProfiles.map(scent => scent + " ")}</TableCell>
                                    </TableRow>
                                )} */}
                            </TableBody>
                        </Table>
                    </ExpandCollapse>
                    <ExpandCollapse
                        title="Working On It"
                        defaultOpenState
                        // headerComponentRenderer={ExpandCollapseCustomHeadingComponent}
                        // className={styles.storybookExpandCollapse}
                    >
                        <Table style={{
                            width: "auto"
                        }} size={Table.sizes.MEDIUM} columns={columns}>
                            <TableHeader>
                                {columns.map((headerCell, index) => 
                                    <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                                )}
                            </TableHeader>
                            <TableBody>
                                {data.map(rowItem => 
                                    <TableRow key={rowItem.id}>
                                        <TableCell>{rowItem.item}</TableCell>
                                        <TableCell>{rowItem.salesAssociate}</TableCell>
                                        <TableCell>{rowItem.scentProfiles}</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </ExpandCollapse>
                    <ExpandCollapse
                        title="Done"
                        defaultOpenState
                        // headerComponentRenderer={ExpandCollapseCustomHeadingComponent}
                        // className={styles.storybookExpandCollapse}
                    >
                        <Table style={{
                            width: "auto"
                        }} size={Table.sizes.MEDIUM} columns={columns}>
                            <TableHeader>
                                {columns.map((headerCell, index) => 
                                    <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                                )}
                            </TableHeader>
                            <TableBody>
                                {data.map(rowItem => 
                                    <TableRow key={rowItem.id}>
                                        <TableCell>{rowItem.item}</TableCell>
                                        <TableCell>{rowItem.salesAssociate}</TableCell>
                                        <TableCell>{rowItem.scentProfiles}</TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </ExpandCollapse>
                </Flex>
            </div>
        </>
    );
};

export default OrdersBoard;
