import React from "react";
import { useHistory } from "react-router-dom";
import { 
    Flex, 
    Button, 
    Table, 
    TableEmptyState, 
    TableErrorState, 
    TableHeader, 
    TableHeaderCell,
    TableBody,
    TableRow,
    TableCell } from "monday-ui-react-core";
import "../App.css";

const OrdersBoard = () => {
    const history = useHistory();
    const homepage = () => (
        history.push("/")
    )

    const columns = [{
        id: "item",
        title: "Item",
        width: 180,
        loadingStateType: "medium-text"
      }, {
        id: "salesAssociate",
        title: "Sales Associate",
        width: 200,
        loadingStateType: "long-text"
      }, {
        id: "scentProfiles",
        title: "Scent Profiles",
        width: 200,
        loadingStateType: "medium-text"
      }];

      const data = [{
        id: 1,
        item: "Pine",
        salesAssociate: "Zack",
        scentProfiles: "Pine, Mint, Grass"
      }, {
        id: 2,
        item: "Spring",
        salesAssociate: "Zack",
        scentProfiles: "Flowers, Breeze, Trees"
      }]

    return (
        <>
            <div className="App">
                <Button className="Back" onClick={homepage}>
                    Back to Home
                </Button>
                <Table style={{
                        width: "auto"
                    }} size={Table.sizes.MEDIUM} columns={columns}>
                    <TableHeader>
                        {columns.map((headerCell, index) => 
                            <TableHeaderCell key={index} title={headerCell.title} icon={headerCell.icon} infoContent={headerCell.infoContent} />
                        )}
                    </TableHeader>
                    <TableBody>
                        {data.map(rowItem => 
                            <TableRow key={rowItem.id}>
                                <TableCell>{rowItem.sentOn}</TableCell>
                                <TableCell>{rowItem.subject}</TableCell>
                            </TableRow>
                        )}
                    </TableBody>
                </Table>
            </div>
        </>
    );
};

export default OrdersBoard;
